import { useParams } from "react-router-dom";
import UseFetch from "./useFetch";

const AnimeData = () => {

    const {id} = useParams();
    const {data:anime, error } = UseFetch("http://localhost:8000/anime/" + id);
    console.log(id);
    return ( 
        <div className="animeData">
            <h1>{anime.title}</h1>
            <div>
                <img src={anime.pic} />
            </div>
            <div>
                {anime.data}
            </div>
        </div>
     );
}
 
export default AnimeData;
